package by.tsvirko.task06.service.impl.book;import by.tsvirko.task06.entity.Book;import by.tsvirko.task06.service.FileBookService;import org.apache.logging.log4j.LogManager;import org.apache.logging.log4j.Logger;import java.io.*;public class FileBookServicempl implements FileBookService {    private static final Logger logger = LogManager.getLogger(FileBookServicempl.class);    /**     * Writes result objects to user file     *     * @param book     * @param fileName     * @throws IOException     */    @Override    public void writeResult(Book book, String fileName) throws IOException {        try {            FileOutputStream f = new FileOutputStream(new File(fileName), true);            ObjectOutputStream o = new ObjectOutputStream(f);            o.writeObject(book);            o.close();            f.close();        } catch (FileNotFoundException e) {            logger.debug("file does not exist");            throw new FileNotFoundException("file does not exist");        } catch (IOException e) {            logger.debug("Error initializing stream");            throw new IOException("Error initializing stream");        }    }}